import React, { Component } from 'react';

export default class TaskForm extends Component {



    render(){

        return (
            <form onSubmit={this.onSubmit}>
                <input 
                type="text" 
                name= "title"
                placeholder="Escribe una nueva tarea" 
                />
                <br />
                <br />
                <textarea 
                name= "description"
                placeholder="Escribe una descripcion" 
               ></textarea>
                <button type="submit">
                    Enviar
                </button>
            </form>
        )
    }
}
